import React, { useState } from 'react';
import Button from 'react-bootstrap/Button'
import responseClients from '../data/clients';
import ClientLi from '../Component/clientLi';
import '../CSS/ClientsList.css';

function ClientList() {
  const [filter, setFilter] = useState('John');
  let allClients = responseClients.clients
  const filterByName = ({ target }) => {
    allClients = allClients
      .filter((client) => client.name.includes(target.value))
  }
  return (
    <div className="client-list-container">
      <header>
        <h1>Clientes Cadastrados</h1>
        <Button>
          novo cliente
        </Button>
      </header>
      <hr/>
      <section className="filters-container">
      {/* <select>
        <option selected value="coco">Filtra por Nome</option>
        <option value="laranja">Filtrar por CPF/CNPJ</option>
        <option value="limao">Filtrar por CEP</option>
      </select> */}
        <input
         type="text"
         placeholder="filtrar por nome"
         onInput={ filterByName }
        />
        <input type="text" placeholder="filtrar por CPF/CNPJ"/>
        <input type="text" placeholder="filtrar por CEP"/>
      </section>
      <hr/>
      <ul>
        { allClients.map((client) => <ClientLi client={ client } />) }
      </ul>
    </div>
  )
}

export default ClientList;
