import React, { useState } from 'react';
import Button from 'react-bootstrap/Button'
import responseClients from '../data/clients';
import ClientLi from '../Component/clientLi';
import '../CSS/ClientsList.css';

function ClientList() {
  const [filter, setFilter] = useState('');
  const [filterStatus, setFilterStatus] = useState('ativo')
  let allClients = responseClients.clients
    .filter((value) => {
      const ceps = value.address.map((add) => add.CEP)
      if (ceps.filter((cep) => cep.includes(filter)).length) return true
      return value.name.toLowerCase().includes(filter) ||  value.cpf_cnpj.includes(filter)
    })
    .filter((value) => { 
      if (filterStatus === 'todos') return true; 
      return value.status === filterStatus
     })

  const filterClient = ({ target }) => {
    setFilter(target.value.toLowerCase())
  }
  const filterByStatus = ({ target: { value } }) => {
    setFilterStatus(value)
  }
  return (
    <div className="client-list-container">
      <header>
        <h1>Clientes Cadastrados</h1>
        <Button>
          novo cliente
        </Button>
      </header>
      <hr/>
      <section className="filters-container">
        <labe>Filtrar por: 
          <input
          type="text"
          placeholder="nome, CPF/CNPJ ou CEP:"
          onInput={ filterClient }
          />
        </labe>
        <div>
          <span>status: </span>
          <label>
            <input type="radio" value="ativo" name="status" onClick={ filterByStatus } checked/>
            &nbsp;ativo
          </label>
          <label>
            <input type="radio" value="inativo" name="status" onClick={ filterByStatus }/>
            &nbsp;inativo
          </label>
          <label>
            <input type="radio" value="todos" name="status" onClick={ filterByStatus }/>
            &nbsp;todos
          </label>
        </div>
      </section>
      <hr/>
      <ul>
        { allClients.map((client) => <ClientLi client={ client } />) }
      </ul>
    </div>
  )
}

export default ClientList;
