import React from 'react';
import moment from 'moment'
import Moment from 'react-moment';
import Button from 'react-bootstrap/Button';
import 'moment-timezone';
import clientList from '../data/clients';
import addressById from '../data/address';
import '../CSS/ClientHome.css';
import AddressItem from '../Component/AddressItem';

function ClientHome(data) {
  const { match: { params: { clientId } } } = data;
  const client = clientList.clients.find((value) => value.clientId === Number(clientId));
  const address = addressById.address;

  return(
    <div className="client-home-container">
      <header>
        <div>
          <h1>
            { client.name }&nbsp;
            <p className={ client.status }/>&nbsp;
            <Button variant="outline-info" size="sm">
              {(client.status === 'ativo') ? 'desativar cliente': 'ativar cliente'}
            </Button>
          </h1>
          <p>Idade:
            { moment()
              .diff(`${client.birth_date}`, 'years') } anos
          </p>
        </div>
        <div>
          <p>CPF/CNPJ: { client.cpf_cnpj }</p>
          <p>Cadastrado em: <Moment format="DD/MM/YYYY">{ client.register }</Moment></p>
        </div>
      </header>
      <hr/>
      <section className="address-container">
        <div>
          <h5>Endereços cadastrados</h5>
          <Button variant="outline-primary">Novo endereço</Button>
        </div>
        <hr/>
        <ul>
          { address.map((value) => <AddressItem value={ value } />) }
        </ul>
      </section>
    </div>
  )
}

export default ClientHome;
